    const APIKey = '76e81f20536a80f8fcc25c183ec6ec0a';
    const lat = 44.651070;
    const lon = -63.582687;
    const [data, setDate] =useState(null);
    useEffect(() => {
         fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${APIKey}`)
        .then(response => response.json())
